import React from "react";
import {Link} from "react-router-dom";
import {connect} from 'react-redux'
import { ReactComponent as Cart} from "assets/svg/supermarket.svg"

import {
    getTotalBasketCount,
    getTotalBasketPrice
} from 'selectors'


const BasketCart = ({totalBasketCount, totalPrice}) => {
    return(
        <div className='cart'>
            <Link
                to='/basket'
                id='dLabel'
                className='btn btn-goToCart'
            >
                <span className='btn_Cart'>{totalBasketCount} item(s) - ${totalPrice}</span>
                <Cart className="nav-shop-cart"/>
            </Link>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        totalBasketCount: getTotalBasketCount(state),
        totalPrice: getTotalBasketPrice(state)
    }
}

export default connect(mapStateToProps,null) (BasketCart)