import React from "react";
import {connect} from 'react-redux';
import {Link} from "react-router-dom";
import {withRouter} from "react-router";
import {compose} from "redux";
import classNames from 'classnames'

import {getCategories, getActiveCategoryId} from 'selectors'

const Categories = ({categories, activeCategoryId}) => {

    const getActiveState = (category) => activeCategoryId === category.id;

    const renderCategory = (category, index) => {
        const linkClass = classNames({
            'link-block': true,
            'active': getActiveState(category)
        });

        return (
            <Link
                to={`/categories/${category.id}`}
                className='link-category'
                key={index}
            >
                <div className={linkClass}>
                    <span className='list-categories-item'>{category.name}</span>
                </div>
            </Link>
        )
    };

    const renderAllCategory = () => {
        let empty = undefined;
        const getActiveState = () => activeCategoryId === empty || null;
        const linkClass = classNames({
            'link-block': true,
            'active': getActiveState()
        });

        return (
            <Link
                to='/'
                className='link-category'
             >
                <div className={linkClass}>
                    <span className='list-categories-item'>ALL</span>
                </div>
            </Link>
        )
    };

    return (
        <div className='categories'>
            <div className='categories-header'>
                <h5>CATEGORIES</h5>
            </div>
            <div className='categories-list'>
                {renderAllCategory()}
                {categories.map((category, index) => renderCategory(category, index))}
            </div>
        </div>
    )
};

const mapStateToProps = (state, ownProps) => ({
    categories: getCategories(state),
    activeCategoryId: getActiveCategoryId(ownProps)
});

export default compose(
    withRouter,
    connect (
        mapStateToProps,
        null
    )
)(Categories)