import React, {Component} from "react";
import {connect} from 'react-redux';
import {searchPhone} from 'actions';
import { ReactComponent as SearchIcon} from "assets/svg/search.svg"

class SearchComp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: '' || props.searchRequest,
        };
    }

    handleChange = (event) => {
        this.setState({value: event.target.value});
    };

    handleSubmit = (event) => {
        event.preventDefault();
        this.props.searchPhone(this.state.value)
    };

    render() {
        const { value } = this.state;
        return (
            <div className='input-group'>
                <form onSubmit={this.handleSubmit}>
                    <div className="input-group mb-2">
                        <div className="input-group-prepend">
                            <button onClick={this.handleSubmit} className="input-group-text">
                                <SearchIcon className="svg-search"/>
                            </button>
                        </div>
                        <input type="text"
                               className="form-control"
                               id="inlineFormInputGroup"
                               placeholder="Search..."
                               onChange={this.handleChange}
                               value={value}
                        />
                    </div>
                </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    searchRequest: state.phonesPage.search
});

const mapDispatchToProps = {
    searchPhone
};

export default connect(mapStateToProps, mapDispatchToProps)(SearchComp)