import React from "react";
import {connect} from 'react-redux'
import {getTotalBasketPrice, getBasketPhonesWithCount} from "../../selectors";
import {removePhoneFromBasket, cleanBasket, basketCheckout} from 'actions'
import {Link} from "react-router-dom";
import Nav from 'component/navigation'
import { ReactComponent as Trash} from "assets/svg/bin.svg"

const Basket = ({phones, totalPrice, removePhoneFromBasket, basketCheckout, cleanBasket}) => {
    const isBasketEmpty = phones.length === 0;

    const renderSidebar = () => (
        <div className='basket-block-buttons'>
            <Link
                to='/'
                className='btn btn-basket-static'
            >
                <span>Continue shopping</span>
            </Link>
            {!isBasketEmpty &&
                <div>
                    <div className='total-user-checkout'>
                        <p>Total:</p>
                        <p className='total-price'>${totalPrice}</p>
                    </div>
                    <button className='btn btn-basket btn-success' onClick={() => basketCheckout(phones)}>Checkout</button>
                    <button className='btn btn-basket btn-basket-danger' onClick={cleanBasket}>Clear Basket</button>
                </div>
            }
        </div>
    );

    const renderContent = () =>(
        <div>
            {isBasketEmpty && <div>Your shopping cart is empty</div>}
            <div className='table-responsive'>
                <table className='table-basket'>
                    <tbody>
                        {phones.map((phone, index) => (
                            <tr
                                key={index}
                                className='item-checout'
                            >
                                <td className='first-column-checkout'>
                                    <img
                                        className='table-basket-img'
                                        src={phone.image}
                                        alt={phone.name}
                                    />
                                </td>
                                <td>{phone.name}</td>
                                <td>${phone.price}</td>
                                <td>{phone.count}</td>
                                <td>
                                    <span onClick={() => removePhoneFromBasket(phone.id)} className='delete-cart'>
                                        <Trash className='delete-product' />
                                    </span>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );

    return (
        <div className='view-container'>
            <Nav/>
            <div className='container'>
                <div className='row'>
                    <div className='col-md-3 mt-5'>
                        {renderSidebar()}
                    </div>
                    <div className='col-md-9 mt-5'>
                        {renderContent()}
                    </div>
                </div>
            </div>
        </div>
    )
};

const mapStateToProps = state => {
    return {
        phones: getBasketPhonesWithCount(state),
        totalPrice: getTotalBasketPrice(state)
    }
};

const mapDispatchToProps = ({
    removePhoneFromBasket,
    cleanBasket,
    basketCheckout
});

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Basket)