import * as actionType from 'actionTypes'
import { toast } from "react-toastify";

const initialState = [];

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case actionType.ADD_PHONE_TO_BASKET:
            toast.success(
                'Your order already in Cart!'
            )
            return [
                ...state,
                payload
            ]

        case actionType.REMOVE_PHONE_FROM_BASKET: {
            return state.filter(item => item !== payload)
        }

        case actionType.CLEAN_BASKET: {
            return initialState

        }

        default: return state
    }
}